		axios.delete(`http://localhost:3000/sights/${siteData._id}`).then(() => {
			axios.get(`http://localhost:3000/sights`).then((res) => {
				props.setTrips(res.data);
			});
		});
	};

	const handleUpdate = (e, trip) => {
		e.preventDefault();
